}
.site-state-item {
  padding: 0 10px;
}
.sidebar .sidebar-button {
  border-bottom: 1px dotted rgba(167,241,238,0.741);
  border-top: 1px dotted rgba(167,241,238,0.741);
}
.sidebar .sidebar-button button {
  border: 0;
  color: #fc6423;
  display: block;
  width: 100%;
}
.sidebar .sidebar-button button:hover {
  background: none;
  border: 0;
  color: #e34603;
}
.links-of-author {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
}
.links-of-author-item {
  margin: 5px 0 0;
  width: 50%;
}
.links-of-author-item a {
  box-sizing: border-box;
  display: inline-block;
  max-width: 100%;
  overflow: hidden;
  padding: 0 5px;
