  padding: 20px;
}
@media (max-width: 991px) {
  .main-inner {
    padding: 20px;
  }
}
.sub-menu {
  border-bottom: 1px solid rgba(167,241,238,0.741);
}
.post-block:first-of-type {
  padding-top: 20px;
}
@media (max-width: 767px) {
