    box-shadow: 0 0 0 rgba(0,0,0,0);
  }
}
.site-meta {
  padding: 20px 0;
}
.brand {
  padding: 0;
}
.site-subtitle {
  margin: 10px 10px 0;
}
@media (min-width: 768px) and (max-width: 991px) {
  .site-nav-toggle,
  .site-nav-right {
    display: flex;
    flex-direction: column;
    justify-content: center;
  }
}
.site-nav-toggle .toggle,
.site-nav-right .toggle {
  color: #fa7e0d;
}
.site-nav-toggle .toggle .toggle-line,
.site-nav-right .toggle .toggle-line {
  background: #fa7e0d;
}
@media (min-width: 768px) and (max-width: 991px) {
  .site-nav {
    --scroll-height: 0;
    height: 0;
    overflow: hidden;
    transition: height 0.2s ease-in-out;
  }
  body:not(.site-nav-on) .site-nav .animated {
    animation: none;
  }
  body.site-nav-on .site-nav {
    height: var(--scroll-height);
  }
}
.menu .menu-item {
  display: block;
  margin: 0;
}
.menu .menu-item a {
  padding: 5px 20px;
  position: relative;
  text-align: left;
  transition-property: background-color;
}
@media (max-width: 991px) {
  .menu .menu-item.menu-item-search {
    display: none;
  }
}
.menu .menu-item .badge {
  background: rgba(167,241,238,0.741);
  border-radius: 10px;
  color: rgb(241, 167, 167);
  float: right;
  padding: 2px 5px;
  text-shadow: 1px 1px 0 rgba(0,0,0,0.1);
}
.sub-menu {
  margin: 0;
  padding: 6px 0;
}
.sub-menu .menu-item {
  display: inline-block;
}
.sub-menu .menu-item a {
  background: transparent;
  margin: 5px 10px;
  padding: initial;
}
.sub-menu .menu-item a:hover {
  background: transparent;
  color: #fc6423;
}
.sub-menu .menu-item-active {
  border-bottom-color: #fc6423;
  color: #fc6423;
}
.sub-menu .menu-item-active:hover {
  border-bottom-color: #fc6423;
}
.sidebar {
  margin-top: 8px;
  position: -webkit-sticky;
  position: sticky;
  top: 8px;
  width: 200px;
}
@media (max-width: 991px) {
  .sidebar {
    display: none;
  }
}
.sidebar-toggle {
  display: none;
}
.sidebar-inner {
  background: var(--content-bg-color);
  border-radius: initial;
  box-shadow: initial;
  box-sizing: border-box;
  color: var(--text-color);
}
.site-state-item {
  padding: 0 10px;
}
.sidebar .sidebar-button {
  border-bottom: 1px dotted rgba(167,241,238,0.741);
  border-top: 1px dotted rgba(167,241,238,0.741);
}
.sidebar .sidebar-button button {
  border: 0;
  color: #fc6423;
  display: block;
  width: 100%;
}
.sidebar .sidebar-button button:hover {
  background: none;
  border: 0;
  color: #e34603;
}
.links-of-author {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
}
.links-of-author-item {
  margin: 5px 0 0;
  width: 50%;
}
.links-of-author-item a {
  box-sizing: border-box;
  display: inline-block;
  max-width: 100%;
  overflow: hidden;
  padding: 0 5px;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.links-of-author-item a {
  border-bottom: 0;
  border-radius: 4px;
  display: block;
}
.links-of-author-item a:hover {
  background: var(--body-bg-color);
}
.back-to-top {
  background: rgba(255,255,255,0);
  margin: 0px -10px -18px;
}
.back-to-top.back-to-top-on {
  margin-top: 16px;
}
.main-inner {
  background: var(--content-bg-color);
  box-shadow: initial;
  padding: 20px;
}
@media (max-width: 991px) {
  .main-inner {
    padding: 20px;
  }
}
.sub-menu {
  border-bottom: 1px solid rgba(167,241,238,0.741);
}
.post-block:first-of-type {
  padding-top: 20px;
}
@media (max-width: 767px) {
  .pagination {
    margin-bottom: 10px;
  }
}



