/** 表格样式 **/


table {
    padding: 2;
    word-break: initial;
}
/*
table tr {
    border-top: 1px solid var(--main-6);
    margin: 1;
    padding: 1;
}
*/
table tr:nth-child(2n) {
    background-color: var(--tableevenrow-color);
}
table tr:nth-child(2n+1) {
    background-color: var(--tablerow-color);
}
table tr th {
    font-weight: bold;
    border: 1px solid var(--tableborder-color);
    background-color: var(--tablethbg-color);
    border-bottom: 1;
    text-align: left;
    margin: 1;
    padding: 6px 13px;
}
table tr td {
    border: 1px solid var(--tableborder-color);
    text-align: left;
    margin: 0;
    padding: 6px 13px;
}
table tr th:first-child,
table tr td:first-child {
    margin-top: 1;
}
table tr th:last-child,
table tr td:last-child {
    margin-bottom: 1;
}
table tr:hover {
    background-color: var(--tablefocusrow-color);
}



body img{max-width:100%;box-sizing:content-box;background-color:#fff}
